function cleaveThousand(){$(".cleave-thousand").toArray().forEach(function(a){var e=new Cleave(a,{numeral:!0,numeralPositiveOnly:!0,numeralThousandsGroupStyle:"thousand",numeralDecimalScale:30});$.valHooks[`#${$(a).attr("id")}`]={getRawValue:function(a){const t=""==e.getRawValue()?0:parseFloat(e.getRawValue());return t},setRawValue:function(a){e.setRawValue(parseFloat(a))}}})}function cleaveThousand2Digit(){$(".cleave-thousand-2digit").toArray().forEach(function(a){var e=new Cleave(a,{numeral:!0,numeralPositiveOnly:!0,numeralThousandsGroupStyle:"thousand",numeralDecimalScale:2});$.valHooks[`#${$(a).attr("id")}`]={getRawValue:function(a){const t=""==e.getRawValue()?0:parseFloat(e.getRawValue());return t},setRawValue:function(a){e.setRawValue(parseFloat(a))}}})}function cleaveNpwp(){$(".cleave-npwp").toArray().forEach(function(a){new Cleave(a,{delimiters:[".",".","-","."],blocks:[2,3,3,1,3,3],uppercase:!0,onValueChanged:function(e){let t=/^[\.\-0-9]+$/;if(!e.target.rawValue.match(t)){let t=e.target.value.replace(/[^\.\-0-9]+/gi,"");$(a).val(t)}}})})}$(document).ready(function(){cleaveNpwp(),cleaveThousand(),cleaveThousand2Digit()});